const { width } = Dimensions.get('window');
const CARD_WIDTH = width * 0.9;

const HairstyleGallery = () => {
  const [modalVisible, setModalVisible] = useState(false);
  const [selectedImage, setSelectedImage] = useState(null);
  const [searchQuery, setSearchQuery] = useState('');

  const openModal = (image) => {
    setSelectedImage(image);
    setModalVisible(true);
  };

  const closeModal = () => {
    setModalVisible(false);
    setSelectedImage(null);
  };

  const filteredHairstyles = hairstyles.filter(hairstyle =>
    hairstyle.text.toLowerCase().includes(searchQuery.toLowerCase())
  );

  return (
    <ImageBackground
      source={{ uri: 'https://scontent.fmnl13-2.fna.fbcdn.net/v/t39.30808-6/423247682_805205928291883_4389632146282746689_n.jpg?_nc_cat=106&ccb=1-7&_nc_sid=833d8c&_nc_eui2=AeFJrSRnypADNrVJqHXb06Dl0s4bLwnBGfHSzhsvCcEZ8ZEloiogm3rbkKN_Rf5oPBuHH5SdJxEwHeKLVTT2YKA4&_nc_ohc=2LMIYfhUlSEQ7kNvgEUOEKG&_nc_ht=scontent.fmnl13-2.fna&oh=00_AYCJ26JS-bWzBGWPLZFBVPoaavCBv0Q8RarjQNXgoC2OYA&oe=66F568F4' }}
      style={styles.background}
    >
      <ScrollView contentContainerStyle={styles.container}>
        
        {/* Search Bar */}
        <TextInput
          style={styles.searchBar}
          placeholder="Search for a hairstyle..."
          value={searchQuery}
          onChangeText={setSearchQuery}
        />

        {/* Hairstyle Cards */}
        {filteredHairstyles.map((hairstyle, index) => (
          <TouchableOpacity key={index} onPress={() => openModal(hairstyle.image)}>
            <View style={styles.card}>
              <Image source={{ uri: hairstyle.image }} style={styles.image} />
              <View style={styles.overlay}>
                <Text style={styles.text}>{hairstyle.text}</Text>
              </View>
            </View>
          </TouchableOpacity>
        ))}
      </ScrollView>

      {/* Modal for full image */}
      <Modal visible={modalVisible} transparent={true}>
        <View style={styles.modalContainer}>
          <TouchableOpacity style={styles.modalBackground} onPress={closeModal}>
            <Image source={{ uri: selectedImage }} style={styles.fullImage} />
          </TouchableOpacity>
        </View>
      </Modal>
    </ImageBackground>
  );
};

